class FillYourProfileBlankFormBloc extends Bloc<FillYourProfileBlankFormEvent, FillYourProfileBlankFormState> {FillYourProfileBlankFormBloc(FillYourProfileBlankFormState initialState) : super(initialState) { on<FillYourProfileBlankFormInitialEvent>(_onInitialize); on<ChangeCountryEvent>(_changeCountry); }

